// Parsear respuesta de OpenAI
const response = $input.item.json.message?.content || $input.item.json.choices[0]?.message?.content;

let analisisIA;
try {
  // Extraer JSON de la respuesta (puede venir con markdown)
  const jsonMatch = response.match(/\{[\s\S]*\}/);
  analisisIA = JSON.parse(jsonMatch[0]);
} catch (error) {
  return {
    error: true,
    mensaje: "No se pudo parsear la respuesta de OpenAI",
    respuesta_raw: response
  };
}

// Combinar con datos del email original
