// Obtener datos del anÃ¡lisis de IA
const analisisIA = $input.item.json;

// Determinar quÃ© email usar para la bÃºsqueda
let emailBusqueda = analisisIA.email_cliente;

// Si el cliente mencionÃ³ un email de pago diferente, usarlo
if (analisisIA.email_pago && analisisIA.email_pago !== analisisIA.email_cliente) {
  emailBusqueda = analisisIA.email_pago;
  console.log(`Cliente escribiÃ³ desde ${analisisIA.email_cliente} pero mencionÃ³ email de pago: ${emailBusqueda}`);
}

// Construir query de bÃºsqueda en Stripe
const query = `email:'${emailBusqueda}'`;

return {
  email_busqueda: emailBusqueda,
  query_stripe: query,
  tiene_datos_adicionales: !!(analisisIA.datos_pedido?.fecha_pago || analisisIA.datos_pedido?.monto || analisisIA.datos_pedido?.ultimos_4_digitos),
  ...analisisIA
};

// Obtener datos del anÃ¡lisis de IA
const analisisIA = $('ğŸ“ Parsear Respuesta IA').item.json;

// Obtener datos del cliente de Stripe
const stripeData = $input.item.json;
const customer = stripeData.data[0];

if (!customer) {
  return {
    cumple_politica: false,
    razon: "Cliente no encontrado en Stripe",
    ...analisisIA
  };
}

// Obtener todos los charges del cliente
const charges = customer.charges?.data || [];
const subscriptions = customer.subscriptions?.data || [];

// Timestamps
const ahora = Math.floor(Date.now() / 1000);
const hace30Dias = ahora - (30 * 24 * 60 * 60);

// Buscar el pago inicial de 1â‚¬ (dos cargos de 0.50â‚¬ = 50 centavos cada uno)
const pagosIniciales = charges.filter(charge => 
  charge.amount === 50 && 
  charge.status === 'succeeded'
);

// Buscar suscripciÃ³n activa
const suscripcionActiva = subscriptions.find(sub => 
  sub.status === 'active' || sub.status === 'trialing'
);

// CASO 1: REEMBOLSO PAGO INICIAL (1â‚¬) - NO REEMBOLSABLE
if (analisisIA.tipo_solicitud === "REEMBOLSO_INICIAL") {
  // El pago inicial de 1â‚¬ NUNCA es reembolsable
  return {
    ...analisisIA,
    cumple_politica: false,
    razon: "El pago inicial de 1â‚¬ NO es reembolsable - es contenido digital ya entregado (resultado del test)",
    customer_id: customer.id,
    tipo: "REEMBOLSO_INICIAL",
    accion_sugerida: "Explicar polÃ­tica + ofrecer soporte tÃ©cnico si hubo problemas con el test"
  };
}

// CASO 2: REEMBOLSO SUSCRIPCIÃ“N
if (analisisIA.tipo_solicitud === "REEMBOLSO_SUSCRIPCION") {
  // Buscar Ãºltimo cargo de suscripciÃ³n (> 1â‚¬)
  const ultimoCargo = charges
    .filter(c => c.amount >= 999 && c.status === 'succeeded') // >= 9.99â‚¬
    .sort((a, b) => b.created - a.created)[0];

  if (!ultimoCargo) {
    return {
      ...analisisIA,
      cumple_politica: false,
      razon: "No se encontraron cargos de suscripciÃ³n",
      customer_id: customer.id
    };
  }

  // Motivos vÃ¡lidos para reembolso de suscripciÃ³n
  const motivosValidos = [
    "indisponibilidad",
    "problemas tÃ©cnicos",
    "error de facturaciÃ³n",
    "cargo duplicado",
    "no funciona",
    "caÃ­da",
    "down",
    "bug"
  ];

  const motivoValido = motivosValidos.some(motivo => 
    analisisIA.motivo_solicitud.toLowerCase().includes(motivo)
  );

  // Verificar si fue reportado dentro de 30 dÃ­as
  const dentroDeVentana = ultimoCargo.created > hace30Dias;

  if (motivoValido && dentroDeVentana) {
    return {
      ...analisisIA,
      cumple_politica: true,
      razon: `Motivo tÃ©cnico vÃ¡lido: ${analisisIA.motivo_solicitud}`,
      customer_id: customer.id,
      charge_ids: [ultimoCargo.id],
      subscription_id: suscripcionActiva?.id,
      monto_total: ultimoCargo.amount,
      tipo: "REEMBOLSO_SUSCRIPCION"
    };
  } else {
    return {
      ...analisisIA,
      cumple_politica: false,
      razon: !motivoValido 
        ? "Motivo no cumple con polÃ­tica de reembolsos" 
        : "Reporte fuera de ventana de 30 dÃ­as",
      customer_id: customer.id,
      subscription_id: suscripcionActiva?.id, // Incluir para poder ofrecer cancelaciÃ³n
      ofrecer_cancelacion: !!suscripcionActiva // Flag para saber si ofrecer cancelar
    };
  }
}

// CASO 3: SOLO CANCELACIÃ“N (sin reembolso) - SIEMPRE PERMITIDA
if (analisisIA.tipo_solicitud === "CANCELACION") {
  // Verificar si tiene suscripciÃ³n activa
  if (!suscripcionActiva) {
    return {
      ...analisisIA,
      cumple_politica: false,
      razon: "No tiene suscripciÃ³n activa para cancelar",
      customer_id: customer.id,
      tipo: "CANCELACION"
    };
  }

  return {
    ...analisisIA,
    cumple_politica: true, // SIEMPRE true para cancelaciones
    razon: "Solicitud de cancelaciÃ³n - siempre permitida",
    customer_id: customer.id,
    subscription_id: suscripcionActiva.id,
    tipo: "CANCELACION",
    solo_cancelar: true, // Flag importante: solo cancelar, NO reembolsar
    accion: "Cancelar suscripciÃ³n sin reembolso"
  };
}

// DEFAULT: NO CUMPLE
return {
  ...analisisIA,
  cumple_politica: false,
  razon: "No cumple con criterios de reembolso",
  customer_id: customer.id
};

// Procesar reembolso de cada cargo
const data = $input.item.json;
const chargeIds = data.charge_ids || [];

// Crear array de promesas de reembolso
const reembolsos = [];

for (const chargeId of chargeIds) {
  reembolsos.push({
    charge_id: chargeId,
    customer_id: data.customer_id,
    monto: data.tipo === "REEMBOLSO_INICIAL" ? 50 : data.monto_total,
    email: data.email_cliente
  });
}

return reembolsos.map(r => ({ json: r }));
